import React, { Component } from 'react';
import axios from 'axios';
import Apropos from './apropos/Apropos';
import Recruteur_about from './contact_recruteure/Recruteur_about';

class Annonce extends Component {
    constructor(props) {
		super(props);

		this.state = {
            annonces: [],
            routes: ''
		}
    }
    componentDidMount() {
        this.getannonce();
    }

    getannonce = () => {
    axios.get('annonce')
        .then(response => this.setState({
            annonces: response.data
        }))
    }


    deleteannonce = (annonce) => {
    axios.delete(`/annonces/${annonce.id}`).then(response => {
        if (response.status === 204) {
            this.props.getannonce();
        }
    })
    }

    handlePropos = () => {
        this.setState({
            routes: 1
        })
    }

    handleRecruteure = () => {
        this.setState({
            routes: 2
        })
    }
    render() {
        const annonces = this.state.annonces
        let route;
        if(this.state.routes === 1){
            route = (
                <Apropos/>
            )
        }else if(this.state.routes === 2){
            route = (
                <Recruteur_about/>
            )
        }
        return (
            <>
            {annonces && annonces.map(annonce => {
                return (
                      <>
                        <div className="content-general"></div>
                            <div className="row">
                                <div className="col-md-12 col-sm-12 ">
                                    <div className="panel-content">

                                        <div className="x_panel">
                                            <div className="x_title">
                                                <h2>Liste des Annonces</h2>
                                                <ul className="nav navbar-right panel_toolbox">
                                                <li><a className="collapse-link"><i className="fa fa-chevron-up"></i></a>
                                                </li>
                                                <li><a className="close-link"><i className="fa fa-close"></i></a>
                                                </li>
                                                </ul>
                                                <div className="clearfix"></div>
                                            </div>
                                            <ul className="nav nav-tabs bar_tabs" role="tablist">
                                                <li className="nav-item">
                                                    <a className="nav-link active" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true" onClick={this.handlePropos}>Home</a>
                                                </li>
                                                <li className="nav-item">
                                                    <a className="nav-link" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">Profile</a>
                                                </li>
                                                <li className="nav-item">
                                                    <a className="nav-link" data-toggle="tab" href="#contact" role="tab" aria-controls="contact" aria-selected="false">Contact</a>
                                                </li>
                                            </ul>
                                            <div className="x_content">
                                                <div className="row">
                                                    <div className="col-sm-12">
                                                        <div className="card-box table-responsive">                                                            
                                                            {route}
                                                        </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div> 
                      </>
                )})}
            </>

        );
    }
}

export default Annonce;
