import Axios from 'axios';
import React, { Component } from 'react'
import Description_annonce from './Description_annonce'

export class AccueilAnnonce extends Component {
    state={
        annonces: []
    }

    componentDidMount = () => {
        this.getAnnonces();
    }

    getAnnonces = () => {
        Axios.get('annonce').then(response => {
            this.setState({
                annonces: response.data
            })
        })
    }
    render() {
        const annonces = this.state.annonces
        return (
            <>
              <div className="body">
                <p>Les annonces publier dans le pages d'accueuil</p>
                <div className="clearfix"></div>
                    <div className="col-md-12 col-sm-6  ">
                        <div className="x_panel">
                        <div className="x_title">
                            <h2>Liste des<small>Annonces</small></h2>
                            <div className="clearfix"></div>
                        </div>
                            <div className="x_content">

                                <table className="table">
                                <thead>
                                    <tr>
                                    <th>Date</th>
                                    <th>Poste</th>
                                    <th>Contrat</th>
                                    <th>Description</th>
                                    <th>Action</th>
                                    </tr>
                                </thead>
                            {annonces && annonces.map(annonce => {
                                let accueil;
                                if(annonce.client == true){
                                    accueil = (
                                        <h1>bonjour</h1>
                                    )
                                }
                                return (
                                    <tbody>
                                        <tr>
                                        {/* <th scope="row">{annonce.date_limite} </th>
                                        <td>Chauffeur</td>
                                        <td>CDI</td> */}
                                        <td>{accueil} </td>
                                        <td> <Description_annonce/></td>
                                        <td><ul className="nav navbar-right panel_toolbox">
                                                    <li> <a className="btn btn-danger" role="button">
                                                        <i className="fa fa-trash"></i>
                                                        </a>
                                                    </li>

                                                    <li> <a className="btn btn-primary" role="button">
                                                        <i className="fa fa-paper-plane-o"></i>
                                                        </a>
                                                    </li>
                                                </ul></td>
                                        </tr>
                                    </tbody>
                                    )
                                })}
                                </table>

                            </div>

                        </div>
                    </div> 


                </div>  
            </>
        )
    }
}

export default AccueilAnnonce
