import React, { Component } from 'react'
import axios from 'axios';
import Pie from './Pie';

export class DebutantModal extends Component {
    state={
        choix: '',
        question: '',
        etapes : '',
        questiondebutant: {},
        choixquestiondebutants: []
    }

    handleClick = e => {
        e.preventDefault();
        this.setState({
            etapes: 2
        })
    }

    handleChoix = (e) => {
        this.setState({
            choix: e.target.value
        })
    }

    handleQuestion = e => {
        this.setState({
            question: e.target.value
        })
    }

    handleSbubmit1 = e => {
        e.preventDefault();
        const data = new FormData;
        data.append('debutant_id', this.props.niveau.debutant.id)
        data.append('question', this.state.question)
        axios.post('questiondebutants', data ).then(response => {
            if(response.status == 201){
                this.setState({
                    etapes: 1
                })
            }
        })
    }

    handleSbubmit2 = e => {
        e.preventDefault();
        const formData = new FormData;
        formData.append('questiondebutant_id', this.state.questiondebutant.id )
        formData.append('choix', this.state.choix)
        axios.post('choixquestiondebutants', formData).then(response => {
            console.log(response)
        })
    }

    componentDidMount() {
        this.getNiveau();
    }

    getNiveau = () => {
        axios.get('debutants/1')
            .then(response => {
                console.log(response.data)
                this.setState({
                    questiondebutant: response.data.question
                })

                this.setState({
                    choixquestiondebutants: response.data.choix
                })

            })
    }

    render() {
        var choix;
        if(this.state.choix){
            choix = (
                <>
                    <label>
                        <input type="checkbox"/> {this.state.choix}
                    </label>
                </>
            )
        }

        let etape;
        if(this.state.etapes == 1){
            etape = (
                <div>
                <p className="text-primary">choix de reponse :</p>
                    <div >
                        <form class="input-group" onSubmit={this.handleSbubmit2} >
                            <input type="text" class="form-control" placeholder="Choix sur la question" onChange={this.handleChoix} />
                            <span class="input-group-btn">
                                <button type="submit" class="btn btn-primary">valider</button>
                            </span>
                            <span class="input-group-btn">
                                <button type="button" class="btn btn-success" onClick={this.handleClick} >suivant</button>
                            </span>
                        </form>
                    </div>
                </div>
            )
        }else if(this.state.etapes == 2){
            etape = (
                <h1>response</h1>
            )
        }else{
            etape = (
            <div>

                <form class="input-group" onSubmit={this.handleSbubmit1}>
                    <input type="text" class="form-control" placeholder="Votre question ?" onChange={this.handleQuestion} />
                    <span class="input-group-btn">
                        <button type="submit" class="btn btn-info">suivant</button>
                    </span>
                </form>
            </div>
            )
        }
        return (
            <div>
                <button type="button" class="btn btn-outline-info" data-toggle="modal" data-target="#staticBackdrop">
                    Ajouter des question niveau debutant
                </button>


                <div class="modal fade" id="staticBackdrop" data-backdrop="static" data-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel"  aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title" id="staticBackdropLabel">Modal title</h5>
                        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div class="modal-body" >
                    <div className="container">
                        <div>
                           {etape}
                        </div>
                        <div class="divider-dashed"></div>
                        <div className="text-success">
                            {this.state.question}
                        </div>
                        <div className="right">
                            {choix}
                        </div>

                    </div>  
                    {/* <div className="col-md-5">
                        <Pie data={this.props.niveau} />
                    </div>          */}
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-primary">valider</button>
                    </div>
                    </div>
                </div>
                </div>
            </div>
        )
    }
}

export default DebutantModal;
