import Axios from 'axios'
import React, { Component } from 'react'
import axios from 'axios';

import './nav_bar.css'
export class Top_navigation extends Component {
    state={
        notification: ''
    }

    componentDidMount(){
        this.getNotification();
    }

    getNotification() {
        axios.get('/notification').then(response => {

            this.setState({
                notification: response.data
            })
        })
    }

    handleClick = () => {
        sessionStorage.clear();
        localStorage.clear();
        window.location.reload();
    }

    render() {
        return (
            <div>
                <div class="top_nav">
                <div class="nav_menu">
                    {/* <div class="nav toggle">
                        <a id="menu_toggle"><i class="fa fa-bars"></i></a> 
                    </div> */}
                    <nav class="nav navbar-nav">
                    <ul class=" navbar-right" >
                        <li style={{margin: '5px'}}>
                        <a href="/contact" class="dropdown-toggle info-number" data-toggle="dropdown" aria-expanded="false">
                            <i class="fa fa-envelope-o"></i>
                            <span class="badge bg-green">{this.state.notification} </span>
                        </a>
                        </li>

                        <li class="nav-item dropdown open">

                        <a href="" class="user-profile dropdown-toggle" aria-haspopup="true" id="navbarDropdown" data-toggle="dropdown" aria-expanded="false">
                            <img src="images/img.jpg" alt="" />Monia
                        </a>
                        <div class="dropdown-menu dropdown-usermenu pull-right" aria-labelledby="navbarDropdown">


                        <a class="dropdown-item"  href="#">Help</a>
                        <a class="dropdown-item"  href="#">Profile</a>
                        <a class="dropdown-item"  href="#" onClick={this.handleClick} ><i class="fa fa-sign-out pull-right"></i> Log Out</a>
                        </div>
                        </li>
                    </ul>
                    </nav>
                </div>
                </div>
            </div>
        )
    }
}

export default Top_navigation;
