import React, { Component } from 'react';
import Login from './pages/admin_login/Login'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

export class App extends Component {
  state={
    user: {}
  }

  render() {
    let login;

    if(this.state.user){
      login = (
        <h1>bonjour</h1>
      )
    }else{
      login = (
        <>
          <fieldset className="login">
            <div className="div-login">
                <Formik
                initialValues={{
                    email: '',
                    password: ''
                }}

                onSubmit={(values, { resetForm }) => {
                    axios.post('/admins', values).then(response => {
                        const { action } = this.props;
                        if (response.status === 201) {
                           this.setState({
                             user: response.data
                           })
                        }
                    })
                }
                }
                >
                    <Form>
                        <div className="mb-3">
                            <Field type="email" className="form-input" placeholder="email" name="email" required />

                        </div>
                        <div className="mb-3">

                            <Field type="password" className="form-input" placeholder="password" name="password" required />
                        </div>

                        <button type="submit" className="login-submit">Submit</button>
                    </Form>
                </Formik>
            </div>
          </fieldset>
        </>
      )
    }
    return (
      <div>
        {login}
      </div>
    )
  }
}

export default App;
