import React, { Component } from 'react';
import {BrowserRouter, Switch, Route} from 'react-router-dom';
import axios from 'axios';

class Annonce extends Component {
    constructor(props) {
		super(props);

		this.state = {
			annonces: []
		}
    }
    componentDidMount() {
        this.getannonce();
    }

    getannonce = () => {
    axios.get('annonce')
        .then(response => this.setState({
            annonces: response.data
        }))
    }


    deleteannonce = (annonce) => {
    axios.delete(`/annonces/${annonce.id}`).then(response => {
        if (response.status === 204) {
            this.props.getannonce();
        }
    })
    }
    render() {
        console.log(this.state.annonces)
        const annonces = this.state.annonces
        return (
            <>
            {annonces && annonces.map(annonce => { 
                      <>
                        <div className="content-general"></div>
                            <div class="row">
                                <div class="col-md-12 col-sm-12 ">
                                    <div className="panel-content">

                                        <div class="x_panel">
                                            <div class="x_title">
                                                <h2>Liste des Annonces</h2>
                                                <ul class="nav navbar-right panel_toolbox">
                                                <li><a class="collapse-link"><i class="fa fa-chevron-up"></i></a>
                                                </li>
                                                <li><a class="close-link"><i class="fa fa-close"></i></a>
                                                </li>
                                                </ul>
                                                <div class="clearfix"></div>
                                            </div>
                                            <ul class="nav nav-tabs bar_tabs" role="tablist">
                                                <li class="nav-item">
                                                    <a class="nav-link active" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">{annonce.poste} </a>
                                                </li>
                                                <li class="nav-item">
                                                    <a class="nav-link" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">Profile</a>
                                                </li>
                                                <li class="nav-item">
                                                    <a class="nav-link" data-toggle="tab" href="#contact" role="tab" aria-controls="contact" aria-selected="false">Contact</a>
                                                </li>
                                            </ul>
                                            <div class="x_content">
                                                <div class="row">
                                                    <div class="col-sm-12">
                                                        <div class="card-box table-responsive">
                                                            <h1>bonjuors</h1>
                                                        </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                      </>
                })}
            </>

        );
    }
}

export default Annonce;
