import React, { Component } from 'react'
import axios from 'axios';

export class Disposition extends Component {
    state={
        disponible: ''
    }

    onClick = e => {
        e.preventDefault()
        this.setState({
            disponible: 'non'
        })
        axios.patch(`chauffeurs/${this.props.chauffeur.id}`, this.state)
            .then(resp => console.log(resp))
    }
    render() {
        console.log(this.state)
        let chauffeur;
        if(this.props.chauffeur.disponible == "oui"){

            chauffeur = (
                <>
                    <td>{this.props.chauffeur.prenom} </td>
                    <td>{this.props.chauffeur.categorie_permis} </td>
                    <td>{this.props.chauffeur.adresse} </td>
                    <td>{this.props.chauffeur.province} </td>
                    <td>
                        <button class="badge rounded-pill bg-success" onClick={this.onClick}>Disponible</button>
                    </td>

                </>
            )
        }
        return (
            <>
                {chauffeur}
            </>

        )
    }
}

export default Disposition
